    n = int(sys.stdin.readline())
    A = sys.stdin.readline().strip()

    from functools import lru_cache

    @lru_cache(maxsize=None)
    def dp(pos, h_count, m_count):
        if pos == 2 * n:
            return (0, "")
        # If it's Homer's turn
        if h_count < n:
            val = int(A[pos])
            total, seq = dp(pos + 1, h_count + 1, m_count)
            total += val
            return (total, "H" + seq)
        # If it's Marge's turn
        else:
            val = int(A[pos])
            total, seq = dp(pos + 1, h_count, m_count + 1)
            total += val
            return (total, "M" + seq)

    _, result = dp(0, 0, 0)
    print(result)

