import sys

def main():
    n = int(sys.stdin.readline())
    a = list(map(int, sys.stdin.readline().split()))

    M = max(a)
    m = min(a)

    count_M = a.count(M)
    count_m = a.count(m)

    # Compute second_max_candidate
    if count_M > 1:
        second_max_candidate = M
    else:
        first_index = a.index(M)
        max_val = -float('inf')
        for i in range(len(a)):
            if i == first_index:
                continue
            if a[i] > max_val:
                max_val = a[i]
        second_max_candidate = max_val

    # Compute second_min_candidate
    if count_m > 1:
        second_min_candidate = m
    else:
        first_index = a.index(m)
        min_val = float('inf')
        for i in range(len(a)):
            if i == first_index:
                continue
            if a[i] < min_val:
                min_val = a[i]
        second_min_candidate = min_val

    option1 = second_max_candidate - m
    option2 = M - second_min_candidate
    original_instability = M - m

    result = min(original_instability, option1, option2)
    print(result)

