import itertools

def main():
    n = int(sys.stdin.readline())
    a = list(map(int, sys.stdin.readline().split()))

    # Generate all permutations of the array a
    from itertools import permutations

    # Check if there's a valid permutation
    for perm in permutations(a):
        # Check if it's a derangement
        is_derangement = True
        for i in range(n):
            if perm[i] == a[i]:
                is_derangement = False
                break
        if not is_derangement:
            continue

        # Now check all subsets S (non-empty, not full)
        valid = True
        # Iterate over all possible subset sizes from 1 to n-1
        for k in range(1, n):
            # Generate all combinations of size k
            for subset_indices in itertools.combinations(range(n), k):
                # Compute sum in a
                sum_a = 0
                for idx in subset_indices:
                    sum_a += a[idx]
                # Compute sum in perm
                sum_b = 0
                for idx in subset_indices:
                    sum_b += perm[idx]
                if sum_a == sum_b:
                    valid = False
                    break
            if not valid:
                break
        if valid:
            print(' '.join(map(str, perm)))
            return
    print(-1)

