def main():
    import sys
    input = sys.stdin.read
    N = int(input().strip())

    if N == 1:
        print(-1)
        return

    board = [[0] * N for _ in range(N)]

    # Fill the top-left quadrant with decreasing numbers
    num = N * N // 2
    row, col = 0, N - 1
    while num >= 1:
        board[row][col] = num
        num -= 1
        if col > 0:
            col -= 1
        else:
            row += 1

    # Fill the bottom-right quadrant with increasing numbers
    num = N * N // 2 + 1
    row, col = N - 1, 0
    while num <= N * N:
        board[row][col] = num
        num += 1
        if col < N - 1:
            col += 1
        else:
            row -= 1

    # Print the board
    for row in board:
        print(' '.join(map(str, row)))

