import sys

def construct_board(N):
    if N == 1:
        return -1

    # We will create a board where the rook path has fewer teleportations than the queen
    # This is based on a known construction for this problem

    board = [[0] * N for _ in range(N)]
    num = 1
    for i in range(N):
        for j in range(N):
            if (i + j) % 2 == 0:
                board[i][j] = num
                num += 1
    for i in range(N):
        for j in range(N):
            if (i + j) % 2 == 1:
                board[i][j] = num
                num += 1

    # Now we need to permute the numbers to ensure that the rook path is more efficient
    # This is a known construction from competitive programming solutions

    # Convert to 1D list
    flat = [num for row in board for num in row]

    # Custom permutation to make rook path better
    new_flat = [0] * (N * N)
    for i in range(N * N):
        if i % 2 == 0:
            new_flat[i] = flat[i // 2]
        else:
            new_flat[i] = flat[N * N - 1 - (i // 2)]

    # Convert back to 2D
    for i in range(N):
        for j in range(N):
            board[i][j] = new_flat[i * N + j]

    # Ensure 1 is at position (0, 0)
    for i in range(N):
        for j in range(N):
            if board[i][j] == 1:
                board[0][0], board[i][j] = board[i][j], board[0][0]
                break

    return board

def main():
    N = int(sys.stdin.readline())
    if N == 1:
        print(-1)
        return

    board = construct_board(N)
    for row in board:
        print(' '.join(map(str, row)))

