def construct_board(N):
    if N == 1:
        return -1

    # We will create a board where the rook path has fewer teleportations than the queen
    # Strategy: Create a spiral-like pattern with specific structure to favor rook movement

    board = [[0] * N for _ in range(N)]
    num = 1

    # Fill the board in a way that favors rook's movement (same row or column)
    # First, fill the first row from left to right
    for i in range(N):
        board[0][i] = num
        num += 1

    # Fill the last column from top to bottom
    for i in range(1, N):
        board[i][N-1] = num
        num += 1

    # Fill the last row from right to left
    for i in range(N-2, -1, -1):
        board[N-1][i] = num
        num += 1

    # Fill the first column from bottom to top
    for i in range(N-2, 0, -1):
        board[i][0] = num
        num += 1

    # Now fill the inner part in a way that still helps rook
    # Fill the remaining cells in a spiral or zigzag pattern
    for i in range(1, N-1):
        for j in range(1, N-1):
            if board[i][j] == 0:
                board[i][j] = num
                num += 1

    return board

def main():
    N = int(sys.stdin.readline())
