import sys

def main():
    import sys
    input = sys.stdin.read
    n, mod = map(int, input().split())

    # dp[i][j]: number of permutations of i elements with j inversions
    max_inv = n * (n - 1) // 2
    dp = [[0] * (max_inv + 1) for _ in range(n + 1)]
    dp[1][0] = 1  # One permutation of length 1 with zero inversions

    for i in range(2, n + 1):
        for j in range(i * (i - 1) // 2 + 1):
            for k in range(j + 1):
                dp[i][j] += dp[i - 1][k]
                dp[i][j] %= mod

    # Now calculate the answer
    total = 0
    for i in range(max_inv + 1):
        for j in range(i):
            total += dp[n][i] * dp[n][j]
            total %= mod

    print(total % mod)

