def main():
    import sys
    input = sys.stdin.read().split()
    n = int(input[0])
    mod = int(input[1])

    max_inv = n * (n - 1) // 2
    dp = np.zeros(max_inv + 1, dtype=np.int64)
    dp[0] = 1

    for i in range(1, n + 1):
        new_dp = np.zeros(max_inv + 1, dtype=np.int64)
        for j in range(max_inv + 1):
            if dp[j] == 0:
                continue
            for k in range(0, i):
                if j + k <= max_inv:
                    new_dp[j + k] += dp[j]
        dp = new_dp

    count = dp.tolist()

    total = 0
    for k in range(max_inv + 1):
        for l in range(k + 1, max_inv + 1):
            total += count[k] * count[l]

    result = (total) % mod
    print(result)

