def max_games(n):
    # Precompute the Fibonacci-like sequence until it exceeds n
    fib = [1, 2]
    while True:
        next_val = fib[-1] + fib[-2]
        if next_val > n:
            break
        fib.append(next_val)

    # The maximum k is the index of the last value in fib that is <= n
    # Since fib[0] corresponds to k=0, fib[1] to k=1, etc.
    # So the answer is len(fib) - 1
    # However, we need to check if the last element is <= n
    if fib[-1] > n:
        return len(fib) - 2
    else:
        return len(fib) - 1

