import sys
import math
from collections import defaultdict, deque

def main():
    input = sys.stdin.read
    data = input().splitlines()

    idx = 0
    n, m = map(int, data[idx].split())
    idx += 1

    edges = []
    graph = defaultdict(list)
    for _ in range(m):
        u, v, g, l = map(int, data[idx].split())
        idx += 1
        edges.append((u, v, g, l))
        graph[u].append((v, g, l))
        graph[v].append((u, g, l))

    # Assign values to nodes
    values = [None] * (n + 1)  # 1-based indexing

    def dfs(node, val):
        if values[node] is not None:
            return values[node] == val
        values[node] = val
        for neighbor, g, l in graph[node]:
            if values[neighbor] is None:
                if val % g != 0:
                    return False
                multiple = val // g
                if multiple * g != val or multiple * l != l:
                    return False
                if not dfs(neighbor, multiple * l):
                    return False
            else:
                if values[neighbor] % g != 0:
                    return False
                if (values[neighbor] // g) * g != val or (values[neighbor] // g) * l != l:
                    return False
        return True

    for i in range(1, n + 1):
        if values[i] is None:
            # Try all possible values that are multiples of GCD and divisors of LCM
            # We need to find a starting value for the component
            # Let's pick one edge connected to this node
            found = False
            for neighbor, g, l in graph[i]:
                # The value at i must be a multiple of g and such that value / g divides l
                # So possible values are g * k where k divides l
                for k in range(1, l + 1):
                    if l % k == 0:
                        val = g * k
                        values[i] = val
                        if dfs(i, val):
                            found = True
                            break
                if found:
                    break
            if not found:
                print("NO")
                return

    print("YES")
    print(' '.join(map(str, values[1:])))

if __name__ == "__main__":
    main()

