def min_ternary_string(s):
    # Split the string into three groups: 0s, 1s, and 2s
    zeros = []
    ones = []
    twos = []

    for c in s:
        if c == '0':
            zeros.append(c)
        elif c == '1':
            ones.append(c)
        else:
            twos.append(c)

    # Now build the result string
    res = []
    z_idx = 0
    o_idx = 0
    t_idx = 0

    for c in s:
        if c == '0':
            res.append(zeros[z_idx])
            z_idx += 1
        elif c == '1':
            res.append(ones[o_idx])
            o_idx += 1
        else:
            res.append(twos[t_idx])
            t_idx += 1

    return ''.join(res)

