    result = []
    i = j = k = 0

    while i < len(zeros) or j < len(ones) or k < len(twos):
        # Add as many 0s as possible
        while i < len(zeros) and (j == len(ones) or k == len(twos) or zeros[i] < ones[j]):
            result.append(zeros[i])
