def minimum_ternary_string(s):
    fixed = []
    ones = []
    for c in s:
        if c == '1':
            ones.append(c)
        else:
            fixed.append(c)

    # Find the first occurrence where '0' is followed by '2'
    insert_pos = -1
    for i in range(len(fixed) - 1):
        if fixed[i] == '0' and fixed[i+1] == '2':
            insert_pos = i + 1
            break

    if insert_pos != -1:
        # Insert all ones at the position between fixed[insert_pos-1] and fixed[insert_pos]
        result = []
        for i in range(len(fixed)):
            if i == insert_pos:
                result.extend(ones)
            result.append(fixed[i])
        return ''.join(result)
    else:
        # No 0 followed by 2, append ones at the end
        return ''.join(fixed) + ''.join(ones)

