    ones = []
    for c in s:
        if c == '1':
            ones.append(c)
        else:
            fixed.append(c)

    # Now, insert the ones into fixed list
    # We want to insert all ones before the first '2' in fixed
    # Find the index of the first '2' in fixed
    first_two = -1
    for i in range(len(fixed)):
        if fixed[i] == '2':
            first_two = i
            break

    if first_two == -1:
        # No '2's, so append all ones after the fixed (which are all 0s)
        return ''.join(fixed) + ''.join(ones)
    else:
        # Insert all ones before the first '2'
        return ''.join(fixed[:first_two]) + ''.join(ones) + ''.join(fixed[first_two:])

