import sys

MOD = 10**9 + 7

def main():
    forbidden = {"0011", "0101", "1110", "1111"}
    m = int(sys.stdin.readline())
    S = []
    sequences = set()

    for _ in range(m):
        c = sys.stdin.readline().strip()
        S.append(c)
        current_len = len(S)

        # Check all substrings ending at the current position with lengths 1-4
        for l in range(1, 5):
            if current_len >= l:
                substring = ''.join(S[-l:])
                if substring not in forbidden:
                    # Generate all possible letter sequences for this substring
                    # Using dynamic programming approach
                    dp = {}
                    # dp[i] is a set of sequences for the first i characters
                    dp[0] = {''}
                    for i in range(1, l+1):
                        dp[i] = set()
                        for j in range(1, 5):
                            if i - j >= 0:
                                part = substring[i-j:i]
                                if part not in forbidden:
                                    for seq in dp[i-j]:
                                        if seq == '':
                                            new_seq = part
                                        else:
                                            new_seq = seq + ' ' + part
                                        dp[i].add(new_seq)
                    # Add all sequences from dp[l] to the global set
                    for seq in dp[l]:
                        if seq != '':
                            sequences.add(seq)
        print(len(sequences) % MOD)

