def main():
    lines = [line.rstrip('\n') for line in sys.stdin]
    ptr = 0
    while ptr < len(lines):
        # Skip empty lines
        while ptr < len(lines) and not lines[ptr]:
            ptr += 1
        if ptr >= len(lines):
            break
        n = int(lines[ptr])
        ptr += 1
        grid = []
        for _ in range(n):
            if ptr >= len(lines):
                break
            grid.append(lines[ptr])
            ptr += 1
        # Process the grid
        diagonal = set()
        non_diagonal = set()
        for i in range(n):
            for j in range(n):
                if i == j or i + j == n - 1:
                    diagonal.add(grid[i][j])
                else:
                    non_diagonal.add(grid[i][j])
        if len(diagonal) != 1:
            print("NO")
        else:
            d_char = diagonal.pop()
            if len(non_diagonal) != 1:
                print("NO")
            else:
                n_char = non_diagonal.pop()
                if d_char != n_char:
                    print("YES")
                else:
                    print("NO")

