import math

def main():
    n = int(input())
    A = input().strip()
    B = input().strip()

    m = len(A)
    k = len(B)

    def compute_lcm(a, b):
        return a * b // math.gcd(a, b)

    lcm_length = compute_lcm(m, k)

    nik_cycle = 0
    pol_cycle = 0

    for i in range(lcm_length):
        a = A[i % m]
        b = B[i % k]
        if a == b:
            continue
        if (a == 'R' and b == 'S') or (a == 'S' and b == 'P') or (a == 'P' and b == 'R'):
            pol_cycle += 1
        else:
            nik_cycle += 1

    full_cycles = n // lcm_length
    remainder = n % lcm_length

    nik_remain = 0
    pol_remain = 0

    for i in range(remainder):
        a = A[i % m]
        b = B[i % k]
        if a == b:
            continue
        if (a == 'R' and b == 'S') or (a == 'S' and b == 'P') or (a == 'P' and b == 'R'):
            pol_remain += 1
        else:
            nik_remain += 1

    total_nik = full_cycles * nik_cycle + nik_remain
    total_pol = full_cycles * pol_cycle + pol_remain

    print(total_nik, total_pol)

if __name__ == "__main__":
    main()

