import math

def main():
    n = int(input())
    A = input().strip()
    B = input().strip()
    m = len(A)
    k = len(B)

    def get_result(a, b):
        if a == b:
            return (0, 0)
        if (a == 'R' and b == 'S') or (a == 'S' and b == 'P') or (a == 'P' and b == 'R'):
            return (0, 1)
        else:
            return (1, 0)

    def compute_lcm(a, b):
        return a * b // math.gcd(a, b)

    lcm = compute_lcm(m, k)

    per_period_a = 0
    per_period_b = 0

    for i in range(lcm):
        a_move = A[i % m]
        b_move = B[i % k]
        res_a, res_b = get_result(a_move, b_move)
        per_period_a += res_a
        per_period_b += res_b

    full_periods = n // lcm
    rem = n % lcm

    total_a = full_periods * per_period_a
    total_b = full_periods * per_period_b

    for i in range(rem):
        a_move = A[i % m]
        b_move = B[i % k]
        res_a, res_b = get_result(a_move, b_move)
        total_a += res_a
        total_b += res_b

    print(total_a, total_b)

if __name__ == "__main__":
    main()

