import math

def is_snowflake_possible(n):
    if n == 1:
        return False
    # We need to check if there exists k > 1 such that n can be represented as a snowflake
    # A snowflake has the form: 1 + k + k*(k-1) + k*(k-1)^2 + ... + k*(k-1)^{m}
    # This is a geometric series with first term k and common ratio (k-1)
    # So total vertices = 1 + k * ((k-1)^{m+1} - 1) / (k-2)
    # Try all possible k from 2 up to sqrt(n) + 1
    for k in range(2, int(math.isqrt(n)) + 2):
        if k == 2:
            # For k=2, it's 1 + 2 + 2*1 + 2*1^2 + ... + 2*1^{m} = 1 + 2*(m+1)
            # So n - 1 must be even
            if (n - 1) % 2 == 0:
                return True
        else:
            # General case: 1 + k * ((k-1)^{m+1} - 1) / (k-2)
            # Try m starting from 0
            total = 1
            while True:
                # Compute next term
                term = k * ((k-1)**(total - 1) - 1) // (k - 2)
                if total + term > n:
                    break
                total += term
                if total == n:
                    return True
    return False

