    notes = ['C', 'C#', 'D', 'D#', 'E', 'F', 'F#', 'G', 'G#', 'A', 'B', 'H']
    return notes.index(note)

def semitone_distance(a, b):
    a_idx = note_to_index(a)
    b_idx = note_to_index(b)
    if a_idx <= b_idx:
        return b_idx - a_idx
    else:
        return 12 - a_idx + b_idx

def classify_chord(notes):
    # Generate all permutations of the three notes
    from itertools import permutations
    for perm in permutations(notes):
        x, y, z = perm
        d1 = semitone_distance(x, y)
        d2 = semitone_distance(y, z)
        if d1 == 4 and d2 == 3:
            return "major"
        if d1 == 3 and d2 == 4:
            return "minor"
    return "strange"

def main():
    import sys
    input_line = sys.stdin.read().strip()
    notes = input_line.split()
    result = classify_chord(notes)
    print(result)

