        # Check if suffix_candidate[:n-2] == prefix_candidate[1:]
        if len(prefix_candidate) != n-1 or len(suffix_candidate) != n-1:
            return None
        if suffix_candidate[:n-2] == prefix_candidate[1:]:
            s_candidate = prefix_candidate + suffix_candidate[-1]
            # Now check all input strings are prefix or suffix of s_candidate
            for s in strings:
                if not (s_candidate.startswith(s) or s_candidate.endswith(s)):
                    return None
            return s_candidate
        else:
            return None

    s_candidate = None
    # Try A as prefix, B as suffix
    s_candidate = check_and_build(A, B)
    if s_candidate is None:
        # Try B as prefix, A as suffix
        s_candidate = check_and_build(B, A)

    # Now generate the output
    result = []
    for s in strings:
        if s_candidate.startswith(s):
            result.append('P')
        else:
            result.append('S')

    print(''.join(result))

if __name__ == "__main__":
    main()

import sys

def solve():
    import sys
    input = sys.stdin.read
    data = input().splitlines()

    n = int(data[0])
    strings = data[1:]

    # Try all possible candidate strings of length n
    # We'll try to find a string that matches the given prefixes and suffixes
    # We can use the fact that the correct string must be such that:
    # - All its prefixes (length 1 to n-1) are in the list
    # - All its suffixes (length 1 to n-2) are in the list (since the full string is not included)

    from itertools import product

    # Generate all possible strings of length n using lowercase letters
    # This is feasible since n <= 100, but generating all is not feasible.
    # Instead, we can use the fact that for each string in the input, it must be a prefix or suffix of the actual string.

    # Let's find the unique set of all prefixes and suffixes
    all_strings = set(strings)

    # Try all possible candidates that can be formed by combining the strings
    # Since the actual string s must have all the given strings as its prefixes or suffixes,
    # we can try to build s by checking which string can be the full string.

    # Try each string in the input as a candidate for the full string
    for candidate in strings:
        if len(candidate) == n:
            # Check if all proper prefixes and suffixes are present in the input
            valid = True
            for i in range(1, n):
                prefix = candidate[:i]
                suffix = candidate[-i:]
                if prefix not in all_strings or suffix not in all_strings:
                    valid = False
                    break
            if valid:
                # Found a valid candidate string
                # Now determine which strings are prefixes and which are suffixes
                result = []
                for s in strings:
                    if len(s) == n:
                        continue  # skip the full string (it's not part of the input)
                    if s == candidate[:len(s)]:
                        result.append('P')
                    elif s == candidate[-len(s):]:
                        result.append('S')
                    else:
                        # This shouldn't happen as per problem statement
                        result.append('P')  # fallback
                print(''.join(result))
                return

    # If no candidate was found with length n, we need to generate possible strings
    # However, this is inefficient for large n. Instead, we can try to build the string from known parts.

    # Try building the string from the longest prefix and suffix
    # Find the longest prefix and suffix
    max_len = n - 1
    for s in strings:
        if len(s) == max_len:
            long_prefix = s
            break
    for s in strings:
        if len(s) == max_len and s != long_prefix:
            long_suffix = s
            break

    # Try to combine them to form the string
    for i in range(1, n):
        if long_prefix[:i] == long_suffix[-i:]:
            overlap = i
            break
    else:
        overlap = 0

    candidate = long_prefix + long_suffix[overlap:]
    if len(candidate) == n:
        # Check if all proper prefixes and suffixes are present
        valid = True
        for i in range(1, n):
